import React from "react";
import axios from 'axios';

class CategoryForm extends React.Component { 
    state = {
        name: ''
    }

    handleChange = (event) => {
        this.setState({ name: event.target.value });
    }

    handleSubmit = (event) => {
        document.getElementById("categoryform").style.display= "none";
        document.getElementById(event.target.categoryname.id).style.display= "none";
        document.getElementById("addbutton").style.display = "block";

        event.preventDefault();
        const input = event.target.categoryname.value
        const id = event.target.categoryname.id


        if (input.indexOf(" ")>0 || input.length>0){

            if (id != "categoryform"){
                axios.post('http://localhost:8000/category-update/'+id+"/", { name : this.state.name })
                .then(res => {
                    console.log(this.state.name)
                    console.log(res);
                    console.log(res.data);
                })
            }
            else{

                axios.post('http://localhost:8000/category-create/', { name : this.state.name })
                .then(res => {
                    console.log(this.state.name)
                    console.log(res);
                    console.log(res.data);
                })
            }
        }
        else{
            console.log("Not valid input")
        }
    }

    render() {
        const textboxstyle ={
            width: "80%",
            "padding" : "3px 13px",
            "box-sizing": "border-box",
            "border": "3px",
            "margin-left":"13px",
            "margin-bottom":"20px",
            "border-radius": "4px",
        }

        const formstyle = {
            "display":"none"
        }

        const buttonstyle ={
            "margin-left" : "13px"
        }

        return (
        <div style={formstyle}>
        <form onSubmit={this.handleSubmit}  id={this.props.id}>
            <input
                type="text"
                name="categoryname"
                ref={node => (this.inputNode = node)}
                style = {textboxstyle}
                onChange={this.handleChange}
                value={this.props.value}
                id = {this.props.id}
            />
            <button style={buttonstyle} class="btn btn-sm btn-outline-success" type="submit">ADD</button>
        </form>
        <button style={buttonstyle} class="btn btn-sm btn-outline-success" type="submit">ADD</button>
        </div>
        )
    }
}

export default CategoryForm
