    let rooturl = "http://localhost:8000/"
    let [cartList,setCartList] = useState([])
    let [isFetched,setIsFetched]= useState(true)


    let fetchCart=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).access
        let data ;
        axios.get(rooturl+'api/cart-list/',
        {
            headers: {
              'Content-Type' : 'application/json',
              'Authorization': 'Bearer '+String(token)
            }
        })
        .then(res => {
            data = res.data;
            setCartList(data)
        })
        .catch(err => {
            console.log(err)
        })
        if (isFetched){
            setIsFetched(false)
        }
    }

    let findDetails=(id)=>{
        let data;
        axios.get(rooturl+'api/product-detail/'+id+"/")
        .then(res => {
        data = res.data;
        setProduct({product:data})
        })
    }


    useEffect(()=>{
        if (isFetched){
            fetchCart()
        }
        let cards  = []
        for(let i=0;i<cartList.length;i++){
            findDetails(cartList[i].product) 
