import React from "react";
import { useContext } from "react";
import CartContext from "../context/CartContext";

export default function CartPage(){
    let {cartDetail} = useContext(CartContext)
    let cards  = []
                for(let i=0;i<cartList.length;i++){
                    let data;
                    axios.get(rooturl+'/api/product-detail/'+cartList[i].product+"/")
                    .then(res => {
                        data = res.data;
                        let thisproduct = data
                        cards.push(
                            <CartListCard 
                                to={"/product/"+cartList[i].product+"/"} 
                                image={rooturl+thisproduct.image} 
                                name={thisproduct.name} 
                                price={thisproduct.price} 
                                quantity={cartList[i].count}
                                totalprice={parseInt(thisproduct.price)*parseInt(cartList[i].count)}
                            />
                        );
                    })
                }
                console.log(cards)
    return(
        <main role="main">
            <div className="album py-5 bg-light">
              <div className="container">
                    <div className="row">
                        {cartDetail}
                    </div>
                    <div className="list-group-item d-flex justify-content-between">
                        <h4>Total price : </h4>
                        <h3 style={{color:"green"}}><strong>totalprice INR</strong></h3>
                    </div>
                    <div className="list-group-item d-flex justify-content-between">
                        <a  href=""><button type="button" className="btn btn-lg btn-outline-success updatecart">CHECKOUT</button></a>
                    </div>
                </div>
            </div>
        </main>
    )
}
