import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';

const CartContext = createContext()
export default CartContext;

export  const CartProvider = ({children}) => {

    let rooturl = "http://localhost:8000/api/"
    let [carttList,cartList] = useState([])
    let [isFetched,setIsFetched]= useState(true)


    let fetchCart=()=>{
        let data ;
        axios.get(rooturl+'product-list/')
        .then(res => {
            data = res.data;
            setProductList(data)
        })
        .catch(err => {
            console.log(err)
        })
        if(isFetched){
            setIsFetched(false)
        }
    }


    useEffect(()=>{
        if(isFetched){
            fetchCart()
        }
        let interval = setInterval(() => {
            fetchCart()
        },3000)
        return ()=>clearInterval(interval)
    })

    let contextData ={
        cartList:cartList,
    }


    return(
        <CartContext.Provider value={contextData}>
            {children}
        </CartContext.Provider>
    )
}
