import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';

const CartContext = createContext()
export default CartContext;

export  const CartProvider = ({children}) => {

    let rooturl = "http://127.0.0.1:8000/api/"
    let [carttList,cartList] = useState([])
    let [isFetched,setIsFetched]= useState(true)


    let fetchCart=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).refresh
        let response = fetch(rooturl+'cart-list/',{
        method:'GET',
            headers:{
                'Content-Type' : 'application/json',
                'Authorization' :'Bearer ' + String(token)
            }
        })
        let data = response.json()
        console.log(data)
    }



    useEffect(()=>{
        if(isFetched){
            fetchCart()
        }
    })

    let contextData ={
        cartList:cartList,
    }


    return(
        <CartContext.Provider value={contextData}>
            {children}
        </CartContext.Provider>
    )
}
