createContext,     
useState,
useEffect } from "react";
import axios from 'axios';
import CartListCard from '../components/cartCard'


const CartContext = createContext()
export default CartContext;

export  const CartProvider = ({children}) => {

    let rooturl = "http://localhost:8000"
    let [cartList,setCartList] = useState([])
    let [isFetched,setIsFetched]= useState(true)
    let [product,setProduct]=useState([])
    let [cartDetail,setCartDetail]=useState([])


    let fetchCart=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).access

        if (isFetched){
            setIsFetched(false)
        }
    }



    useEffect(()=>{
        if(isFetched){
            fetchCart()
        }

    })

    let contextData ={
        cartList:cartList,
        cartDetail:cartDetail
    }


    return(
        <CartContext.Provider value={contextData}>
            {children}
        </CartContext.Provider>
    )
}
