import {
    createContext,
    useState,
    useEffect
} from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { rooturl } from "../utils/functions";

const ProductContext = createContext()
export default ProductContext;

export const ProductProvider = ({ children }) => {

    const navigate = useNavigate()
    let [productList, setProductList] = useState([])
    let [isFetched, setIsFetched] = useState(true)
    let [thisProduct, setThisProduct] = useState([])

    let fetchProducts = () => {
        let data;
        axios.get(rooturl + '/api/product-list/')
            .then(res => {
                data = res.data;
                setProductList(data)
            })
            .catch(err => {
                console.log(err)
            })
        if (isFetched) {
            setIsFetched(false)
        }
    }

    let editProduct = (e) => {
        e.preventDefault()

        let data = new FormData();
        data.append('image', e.target.image.files[0]);
        fetch(rooturl+'/api/product-image-update/'+thisProduct+'/', {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`
        },
          body: data
        }).then(response => {
            return response.json();
        }).then(data => {
            console.log(data);
        }).catch((error) => {
            console.error('Error:', error);
        });

        axios.post(rooturl + '/api/product-update/' + thisProduct + "/",{
            name : name,
            description : description,
            price : price,
            category : categories
        }, 
        {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            .then(function(response) {
                console.log(response)
            })
            .catch(function(error) {
                alert(error)
        });
    }


    useEffect(() => {
        if (isFetched) {
            fetchProducts()
            console.log(productList)
        }
    })

    let contextData = {
        productList: productList,
        editProduct: editProduct,
        setThisProduct: setThisProduct,
    }


    return ( 
        <ProductContext.Provider value = { contextData }>
          {children } 
        </ProductContext.Provider>
    )
}
