import React from "react";
import axios from "axios";
import { rooturl } from "../utils/functions";
import { useState } from "react";

export default function ProfilePage(){
    let Token = JSON.parse(localStorage.getItem('authToken')).access
    let data ;
    let [customerDetail,setCustomerDetail] = useState([])
    axios.get(rooturl+'/api/customer-detail/',{
        headers:{
            'Authorization':`Bearer ${Token}`
        }
    })
    .then(res => {
        data = res.data;
        setCustomerDetail(data)
    })
    .catch(err => {
        console.log(err)
    })
    return(
        <div className="container" style={{padding:"20px"}}>
            <div className="card">
                    <img id="profilepic" src="" className="img-fluid profile-image" width="70"/>
                    <div className="container" style={{padding:"50px"}}>
                        <h5 className="" >username : <strong></strong></h5>
                        <h5 className=" " >name : </h5>
                        <p className=" ">email :</p>
                        <p className="">phone : </p>
                        <p className="">Country : </p>
                        <p className="">State: </p>                                  
                        <p className=" ">District : </p>
                        <p className="">Address: </p>
                        <p className="">postalcode: </p>
                    </div>
            </div>
            <div className="middle-container d-flex justify-content-between align-items-center mt-3 p-2">
                <div className="dollar-div px-3">
                    <div className="round-div"><i className="fa fa-dollar dollar"></i></div>
                </div>
            </div>
        </div>
    )
}
