import React from "react";
import { rooturl } from "../utils/functions";
import { useContext } from "react";
import CategoryContext from "../context/CategoryContext";


export default function ProductForm(props){

    let {categories} = useContext(CategoryContext)
    let categorylist = [<Option name="afsfc"/>,<Option name="adfssfe"/>]

    for(let i=0 ; i<categories.length;i++){
      categorylist.push(<Option value={categories[i].id} name={category.categoryname}/>)
    }

    return(
        <form id={props.id} style={props.style}>
          <p>
            <p>Name :</p>
            <input type="text" name='name' className="productformfield" required defaultValue={props.name} />
          </p>
          <p>
            <p>Image :</p>
            <img src={rooturl+props.image} className="productformfield" style={{maxHeight:"300px",marginBottom:"3px",width:"auto"}}></img>
            <input type="file"
                    accept="image/png, image/jpeg" 
                    className="productformfield" 
                    required
                    data-default-file={props.image}/>
          </p>
          <p>
            <p>Description :</p>
            <textarea type="text" name='description' className="productdescriptionfield"  required defaultValue={props.description}  />
          </p>
          <p>
            <p>Price :</p>
            <input type="text" name='price' className="productformfield" required defaultValue={props.price} />
          </p>
          <p>
            <p>Category :</p>
            <select name="category" required className="productformfield" multiple>
            {categorylist}
            </select>
          </p>
          <input className="btn btn-sm btn-outline-success" value="save" type="submit"/>
        </form>
    )

}

function Option(props){
  return(
      <option
      value={props.id}>
      {props.name}
      </option>

  )
}
