import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';
import CartListCard from '../components/cartCard'


const CartContext = createContext()
export default CartContext;

export  const CartProvider = ({children}) => {

    let rooturl = "http://localhost:8000"
    let [cartList,setCartList] = useState([])
    let [isnotFetched,setIsnotFetched]= useState(true)
    let [product,setProduct]=useState([])
    let [cartDetail,setCartDetail]=useState([])


    let fetchCart=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).access
        let data ;
        axios.get(rooturl+'/api/cart-list/',
        {
            headers: {
              'Content-Type' : 'application/json',
              'Authorization': 'Bearer '+String(token)
            }
        })
        .then(res => {
            data = res.data;
            setCartList(data)
        })
        .catch(err => {
            console.log(err)
        })
        if (isFetched){
            setIsFetched(false)
        }
    }



    useEffect(()=>{
        console.log("out")
        if (isFetched){
            console.log("in")
            fetchCart()
            if(cartList.length>0){
                let cards  = []
                for(let i=0;i<cartList.length;i++){
                    let data;
                    axios.get(rooturl+'/api/product-detail/'+cartList[i].product+"/")
                    .then(res => {
                        data = res.data;
                        let thisproduct = data
                        cards.push(
                            <CartListCard 
                                to={"/product/"+cartList[i].product+"/"} 
                                image={rooturl+thisproduct.image} 
                                name={thisproduct.name} 
                                price={thisproduct.price} 
                                quantity={cartList[i].count}
                                totalprice={parseInt(thisproduct.price)*parseInt(cartList[i].count)}
                            />
                        );
                    })
                }
                setCartDetail(cards)
            }
            else{
              setIsFetched(true)
            }
        }


    })

    let contextData ={
        cartList:cartList,
        cartDetail:cartDetail
    }


    return(
        <CartContext.Provider value={contextData}>
            {children}
        </CartContext.Provider>
    )
}
