import React from "react";
import axios from "axios";
import CartListCard from "../components/cartCard";
import { useContext } from "react";
import ProductContext from "../context/ProductContext";
import CartContext from "../context/CartContext";
import { Navigate } from 'react-router';

let rooturl ="http://127.0.0.1:8000"

export function increment(id){
    let Token = JSON.parse(localStorage.getItem('authToken')).access
    axios.get(rooturl+'/api/cart-add/'+id+"/",{
        headers:{
            'Authorization':`Bearer ${Token}`
        }
    })

}

export function decrement(id){
    let Token = JSON.parse(localStorage.getItem('authToken')).access
    axios.get(rooturl+'/api/cart-remove/'+id+"/",{
        headers:{
            'Authorization':`Bearer ${Token}`
        }
    })

}

export default function CartPage(){
    let {productList} = useContext(ProductContext)
    let {cartList}  = useContext(CartContext)
    let  {fetchCart} = useContext(CartContext)
    let totalprice = 0
    let cards = []

    function onIncrement(id){
        increment(id)
        fetchCart()
    }

    function onDecrement(id){
        decrement
    }

    for (let i=0  ; i<cartList.length;i++){
        for (let j=0  ; j<productList.length;j++){
            if(productList[j].id===cartList[i].product){
                let tp = parseFloat(cartList[i].count)*parseFloat(productList[j].price)
                totalprice+=tp
                cards.push(
                    <CartListCard
                    to={'/product/'+productList[j].id+"/"}
                    name ={productList[j].name}
                    image={rooturl+productList[j].image}
                    price = {parseFloat(productList[j].price)}
                    quantity = {cartList[i].count}
                    increment = {()=>onIncrement(productList[j].id)}
                    decrement = {()=>decrement(productList[j].id)}
                    totalprice = {tp}
                    />
                )
            }
        }
    }
    return(
        <main role="main">
            <div className="album py-5 bg-light">
            <div className="container">
                    <div className="row">
                        {cards}
                    </div>
                    <div className="list-group-item d-flex justify-content-between">
                        <h4>Total price : </h4>
                        <h3 style={{color:"green"}}><strong>{totalprice} INR</strong></h3>
                    </div>
                    <div className="list-group-item d-flex justify-content-between">
                        <a  href=""><button type="button" className="btn btn-lg btn-outline-success updatecart">CHECKOUT</button></a>
                    </div>
                </div>
            </div>
        </main>
    )
}
