import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';
import { rooturl } from "../utils/functions";

const CategoryContext = createContext()
export default CategoryContext;

export  const CategoryProvider = ({children}) => {

    let [categories,setCategories] = useState()
    let [isFetched,setIsFetched]= useState(true)
    let [categoryList, SetCategoryList]  = useState

    let fetchCategories=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).access
        axios.get(rooturl+'/api/category-list/',
        {headers:{
            Authorization  :  `Bearer ${token}`
        }})
        .then(res => {
            let data = res.data;
            setCategories(data)

        })
        if(isFetched){
            setIsFetched(false)
        } 
    }

    let setCategoryOption=()=>{
        let categorylist = []
        for(let i=0 ; i<categories.length;i++){
           categorylist.push(<Option value={categories[i].id} name={categories[i].categoryname}/>)
        }
    }


    useEffect(()=>{
        if(isFetched){
            fetchCategories()
        }
    })

    let contextData ={
        categories:categories
    }


    return(
        <CategoryContext.Provider value={contextData}>
            {children}
        </CategoryContext.Provider>
    )
}
