import React from "react";
import { useContext } from "react";
import CategoryContext from "../context/CategoryContext";

export default function CategoryPage(){

    let {categories} = useContext(CategoryContext)

    return(
        <div className="container card" style={{marginTop:"20px"}}>
        <h3 style={{"padding":"27px"}}>Categories</h3>
        <ul className="row">
            {categories? categories.map(category=>(
                <li className="card category">{category.categoryname}</li>           
            )):""}
            <li className="card" id="addnewcategory">Add New Category</li>
        </ul>
        <p>Double click to delete</p>
        <p></p>
        </div>
    );

}
