import React from "react";
import axios from "axios";
import CartListCard from "../components/cartCard";

class CartPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            cartList:[],
            cartDetail:[]
        }
    }


    render(){
        const rooturl = "http://localhost:8000"

        let token = JSON.parse(localStorage.getItem('authToken')).access
        let data ;
        axios.get(rooturl+'/api/cart-list/',
        {
            headers: {
                'Content-Type' : 'application/json',
                'Authorization': 'Bearer '+String(token)
            }
        })
        .then(res => {
            data = res.data;
            this.setState({cartList:data})
        })
        .catch(err => {
            console.log(err)
        })

        let cards = []
        if(this.state.cartList.length>0){
            for(let i=0;i<this.state.cartList.length;i++){
                let thisproduct;
                axios.get(rooturl+'/api/product-detail/'+this.state.cartList[i].product+"/")
                .then(res => {
                    thisproduct = res.data;
                    cards.push(
                        <CartListCard 
                            to={"/product/"+this.state.cartList[i].product+"/"} 
                            image={rooturl+thisproduct.image} 
                            name={thisproduct.name} 
                            price={thisproduct.price} 
                            quantity={this.state.cartList[i].count}
                            totalprice={parseInt(thisproduct.price)*parseInt(this.statecartList[i].count)}
                        />
                    );
                })
            }
        }

        console.log(cartDetail)

        return(
            <main role="main">
                <div className="album py-5 bg-light">
                <div className="container">
                        <div className="row">
                            {cards}
                        </div>
                        <div className="list-group-item d-flex justify-content-between">
                            <h4>Total price : </h4>
                            <h3 style={{color:"green"}}><strong>totalprice INR</strong></h3>
                        </div>
                        <div className="list-group-item d-flex justify-content-between">
                            <a  href=""><button type="button" className="btn btn-lg btn-outline-success updatecart">CHECKOUT</button></a>
                        </div>
                    </div>
                </div>
            </main>
        )
    }
}

export default CartPage
