import CartListCard from "../components/cartCard";

class CartPage extends React.Component{
    rooturl = "http://localhost:8000"
    constructor(props) {
        super(props);
        this.state = {
            cartDetail:[]
        }
    }

    componentDidMount() {
        let rooturl = this.rooturl
        let token = JSON.parse(localStorage.getItem('authToken')).access
        let data ;
        axios.get(rooturl+'/api/cart-list/',
        {
            headers: {
                'Content-Type' : 'application/json',
                'Authorization': 'Bearer '+String(token)
            }
        })
        .then(res => {
            data = res.data;
            let rooturl = this.rooturl
            let cards = []
            let cartList = res.data
            if(cartList.length>0){
                for(let i=0;i<cartList.length;i++){

                    cards.push(
                        <div className="col-md-4">
                            <h1>hello</h1>
                            <CartListCard 
                                to={"/"} 
                                image={""} 
                                name={"asdad"} 
                                price={"999"} 
                                quantity={cartList[i].count}
                                totalprice={parseInt(thisproduct.price)*parseInt(cartList[i].count)}
                            />
                        </div>
                    )

                }
            }
            this.setState({cartDetail:cards});
        })
        .catch(err => {
            console.log(err)
        })

    }


    render(){
        let cartDetail = this.state.cartDetail
        console.log(cartDetail)
        return(
            <main role="main">
                <div className="album py-5 bg-light">
                <div className="container">
                        <div className="row">
                            {cartDetail}
                        </div>
