import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';
import { rooturl } from "../utils/functions";
import { useNavigate } from "react-router-dom";
const CategoryContext = createContext()
export default CategoryContext;


export  const CategoryProvider = ({children}) => {

    let [categories,setCategories] = useState([1,3])
    let [isFetched,setIsFetched]= useState(true)
    let Navigate = useNavigate()

    let fetchCategories=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')) ? 
        JSON.parse(localStorage.getItem('authToken')).access  : null
        axios.get(rooturl+'/api/category-list/',
        {headers:{
            Authorization  :  `Bearer ${token}`
        }})
        .then(res => {
            let data = res.data;
            setCategories(data)
        })
        if(isFetched){
            setIsFetched(false)
        } 
    }


    let contextData ={
        categories:categories
    }

    useEffect(() => {
        if(isFetched){
          fetchCategories()
        }
    })


    return(
        <CategoryContext.Provider value={contextData}>
            {children}
        </CategoryContext.Provider>
    )
}
