import { 
createContext,     
useState,
useEffect } from "react";
import axios from 'axios';

const CartContext = createContext()
export default CartContext;

export  const CartProvider = ({children}) => {

    let rooturl = "http://localhost:8000/api/"
    let [carttList,cartList] = useState([])
    let [isFetched,setIsFetched]= useState(true)


    let fetchCart=()=>{
        let token = JSON.parse(localStorage.getItem('authToken')).access
        console.log(token)
        let data ;
        axios.get(rooturl+'cart-list/',
        {
            headers: {
              'Content-Type' : 'application/json',
              'Authorization': 'Bearear '+String(token)
            }
        })
        .then(res => {
            data = res.data;
            console.log(data)
        })
        .catch(err => {
            console.log(err)
        })
    }


    useEffect(()=>{
        if(isFetched){
            fetchCart()
        }
    })

    let contextData ={
        cartList:cartList,
    }


    return(
        <CartContext.Provider value={contextData}>
            {children}
        </CartContext.Provider>
    )
}
