import React from "react";

export function updateProductstate(thisproduct) {
    console.log(thisproduct)
    this.setState({product: thisproduct})
}


class Product extends React.Component{

    rooturl = "http://localhost:8000"

    constructor(props) {
        super(props);
        this.state = {
            product : 
            {
                "id": 1,
                "name": "HP Pavilion 14, 11th Gen Intel Core i5",
                "image": "/media/media/61LXT2Q6YCL._SL1200__agpl5UB.jpg",
                "description": "Processor: Intel 11th Gen Core i5- 1155G7 (up to 4.5 GHz with Intel Turbo Boost Technology(2g), 8 MB L3 cache, 4 cores, 8 threads)\r\n    Memory: 8 GB DDR4-3200 SDRAM (2 x 4 GB), Upto 16 GB DDR4-3200 (2 x 8 GB)| Storage: 512 GB PCIe NVMe M.2 SSD\r\n    Display: 35.6 cm (14\") diagonal, FHD, IPS, micro-edge, BrightView screen, 250 nits, 157 ppi, 45%NTSC (1920x1080)\r\n    Operating System & Preinstalled Software: Windows 11 Home 64 Plus Single Language | Microsoft Office Home & Student 2019| Alexa Built in| McAfee LiveSafe (30 days free trial as default)\r\n    Graphics & Networking: Intel Iris Xe Graphics| Realtek RTL8822CE 802.11a/b/g/n/ac (2x2) Wi-Fi and Bluetooth 5 combo",
                "price": 61990.0,
                "date_created": "2022-01-20T13:44:44.436282Z",
                "category": [
                    1,
                    10,
                    13,
                    14,
                    15
                ]
            },
        }
        updateProductstate = updateProductstate.bind(this)
    }
    componentDidMount() {

        let data ;
        axios.get(this.rooturl+'/api/product-list/')
        .then(res => {
            data = res.data;
            this.setState({
                productlist : data   
            });
        })
        .catch(err => {
            console.log(err)
        })

    }


    render(){



        return(
            <>
            <br/>
            <div className="container">
                <div className="card mb-4 box-shadow">
                    <div className="select2-container--classNameic">
                        <img className="card-img-top" src={this.rooturl+this.state.product.image}style={{maxHeight:"350px", width:"auto", display: "block", marginLeft: "auto", marginRight: "auto", marginTop:"10px"}} />
                    </div>
                    <div className="card-body">
                        <h3 className="card-text">{this.state.product.name}</h3>
                        <h2 className="card-text" style={{color:"green"}}>Price INR</h2>
                        <br/>
                        <p className ="card-text">Product description here</p>
                        <div className="d-flex justify-content-between align-items-center">
                            <div className="btn-group">
                            <a href=""><button type="button" style={{margin:"1px"}} className="btn btn-sm btn-outline-info">ADD TO CART</button></a>

                            <a href=""><button type="button" style={{margin:"1px"}} className="btn btn-sm btn-outline-warning">EDIT</button></a>
                            <a href="" onclick="return confirm('Are you sure you want to delete this item?');" ><button type="button" style={{margin:"1px"}} className="btn btn-sm btn-outline-danger">DELETE</button></a>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
            </>
        );
    }
}

export default Product
