import React  from "react";
import Button from "./button";
import CategoryForm from "./categoryform";


export default function Sidebar(props){

        const buttonstyle ={
            "margin-top":"6px",
            "margin-left" : "13px"
        }
        const sidebarstyle = {
            width: "180px",
            minHeight: "100%",
        }

       const addCategory=()=>{
           document.getElementById("addcategory").style.visibility = ""

        }


        return(
            <div className="d-flex flex-column flex-shrink-0 p-1 text-white bg-dark"  style={sidebarstyle}>
                <hr/>
                <ul className="nav nav-pills flex-column mb-auto">
                <li><p className="nav-link text-white"><strong>CATEGORIES</strong></p></li>
                    {props.categories}
                    <Button onClick={()=>addCategory()} className="btn btn-sm btn-outline-info" name="ADD MORE" style={buttonstyle}/>
                    <CategoryForm id="addcategory"/>
                </ul>
                <hr/>
            </div>
        );
}
