import React from "react";
import Button from "./button";

class CategoryForm extends React.Component {

    handleSubmit = (event) => {
        event.preventDefault()
        const input = event.target.categoryname.value
        if (input.indexOf(" ")>0){
            console.log(input)
        }
        else{
            console.log("Not valid input")
        }
        document.getElementById("categoryform").style.visibility = "hidden";
        document.getElementById("addbutton").style.visibility = "visible";
    }

    render() {
        const textboxstyle ={
            width: "80%",
            "padding" : "3px 13px",
            "box-sizing": "border-box",
            "border": "3px",
            "margin-left":"13px",
            "margin-bottom":"20px",
            "border-radius": "4px",
        }

        const formstyle = {
            "visibility":"hidden"
        }

        const buttonstyle ={
            "margin-left" : "13px"
        }

        return (
        <form onSubmit={this.handleSubmit} style={formstyle} id={this.props.id}>
            <input
                type="text"
                name="categoryname"
                ref={node => (this.inputNode = node)}
                style = {textboxstyle}
            />
            <button style={buttonstyle} class="btn btn-sm btn-outline-info" type="submit">Submit</button>
        </form>
        )
    }
}

export default CategoryForm
