import React from "react";
import Button from "./button";

export default function CategoryForm(props){
    const textboxstyle ={
        width: "80%",
        "padding" : "3px 13px",
        "box-sizing": "border-box",
        "border": "3px",
        "margin-left":"13px",
        "margin-bottom":"20px",
        "border-radius": "4px",
    }
    const buttonstyle ={
        "margin-left" : "13px"
    }
    return(
        <form style={props.style} id={props.id} onSubmit={props.onSubmit}>
            <input ref={node => (props.inputNode = node)}  name={props.name} type="text" style={textboxstyle} value={props.value}/>
            <Button type="submit" className="btn btn-sm btn-outline-info" name="ADD" style={buttonstyle}/>
        </form>
    );
}
